import React, { useEffect, useState } from 'react';
import axios from 'axios'

const backendUri = 'http://localhost:5001'

function App() {
  const [customExtensions, setCustomExtensions] = useState([]);
  const [newExtension, setNewExtension] = useState('');
  const [fixedExtensions, setFixedExtensions] = useState([]);

  useEffect(() => {
    fetchFixedExtensions();
  }, []);

  const fetchFixedExtensions = async () => {
    try {
      const response = await axios.get('/fixed/list');
      setFixedExtensions(response.data);
    } catch (error) {
      console.log('Axios Error : ', error);
    }
  }


  const handleFixedExtension = async (id, status) => {
    try {
      const response = await axios.post(`${backendUri}/update/fixed`, {id,status})
      const updatedExtensions = fixedExtensions.map((extension) => {
        if (extension.id === id) {
          return { ...extension, status: response.data.status };
        }
        return extension;
      });
      setFixedExtensions(updatedExtensions);
    } catch (error) {
      console.log('Axios Error : ', error);
    }
  }

  const handleAddExtension = () => {
    if (newExtension && newExtension.length <= 20) {
      setCustomExtensions([...customExtensions, newExtension]);
      setNewExtension('');
    }
  };

  const handleDeleteExtension = (index) => {
    const updatedExtensions = customExtensions.filter((_, i) => i !== index);
    setCustomExtensions(updatedExtensions);
  };

  return (
    <div>
      <h2>1번 고정확장자</h2>
      <div>
        {fixedExtensions.length === 0 ? (
          <p>Loading...</p>
        ) : (
          <div>
            {fixedExtensions.map((extension) => (
              <label key={extension.id} style={{ margin: '0 10px 0 0' }}>
                <input
                  type="checkbox"
                  name="extension"
                  value={extension.name}
                  checked={extension.status === 1}
                  onChange={() => handleFixedExtension(extension.id, extension.status)}
                />{' '}
                {extension.name}
              </label>
            ))}
          </div>
        )}
      </div>

      <div>
        <h2>2번 커스텀확장자</h2>
        <input
          type="text"
          value={newExtension}
          onChange={(e) => setNewExtension(e.target.value)}
          maxLength={20}
        />
        <button style={{ background: 'grey', color: 'white' }} onClick={handleAddExtension}>
          + 추가
        </button>
      </div>
      <div>
        <h2>3번</h2>
        {customExtensions.map((extension, index) => (
          <button key={index} onClick={() => handleDeleteExtension(index)}>
            {extension} X
          </button>
        ))}
      </div>
    </div>
  );
}

export default App;
