import React, { useState } from 'react';

function App() {
  const [customExtensions, setCustomExtensions] = useState([]);
  const [newExtension, setNewExtension] = useState('');

  const handleAddExtension = () => {
    if (newExtension && newExtension.length <= 20) {
      setCustomExtensions([...customExtensions, newExtension]);
      setNewExtension('');
    }
  };

  const handleDeleteExtension = (index) => {
    const updatedExtensions = customExtensions.filter((_, i) => i !== index);
    setCustomExtensions(updatedExtensions);
  };

  return (
    <div>
      <div>
        <h2>1번 고정확장자</h2>
        <label>
          <input type="checkbox" name="extension" value="bat" /> bat
        </label>
        <label>
          <input type="checkbox" name="extension" value="cmd" /> cmd
        </label>
        <label>
          <input type="checkbox" name="extension" value="com" /> com
        </label>
        <label>
          <input type="checkbox" name="extension" value="cpl" /> cpl
        </label>
        <label>
          <input type="checkbox" name="extension" value="exe" /> exe
        </label>
        <label>
          <input type="checkbox" name="extension" value="scr" /> scr
        </label>
        <label>
          <input type="checkbox" name="extension" value="js" /> js
        </label>
      </div>
      <div>
        <h2>2번 커스텀확장자</h2>
        <input
          type="text"
          value={newExtension}
          onChange={(e) => setNewExtension(e.target.value)}
          maxLength={20}
        />
        <button style={{ background: 'grey', color: 'white' }} onClick={handleAddExtension}>
          + 추가
        </button>
      </div>
      <div>
        <h2>3번</h2>
        {customExtensions.map((extension, index) => (
          <button key={index} onClick={() => handleDeleteExtension(index)}>
            {extension} X
          </button>
        ))}
      </div>
    </div>
  );
}

export default App;
