import React, { useEffect, useState } from 'react';
import axios from 'axios'

const backendUri = 'http://localhost:5001'

function App() {
  const [customExtensions, setCustomExtensions] = useState([]);
  const [newExtension, setNewExtension] = useState('');

  const fixedNameList = ['bat', 'cmd', 'com', 'cpl', 'exe', 'scr', 'js'];
  const [fixedList, setFixedList] = useState([]);
  const [isChecked, setIsChecked] = useState(false);

  useEffect(() => {

  }, []);

  const fetchFixedExtensions = async () => {
    try {
      const response = await axios.get('/fixed/list');
      setFixedList(response.data);
      console.log('했다!!!')
    } catch (error) {
      console.log('Axios Error : ', error);
    }
  }


  const handleFixedExtension = async (name, isChecked) => {
    try {
      if (isChecked) {
        setFixedList((prev) => [...prev, name])
      }

      if (!isChecked && fixedList.includes(name)) {
        setFixedList(fixedList.filter((item) => item !== name));
      }
      // const response = await axios.post(`${backendUri}/update/fixed`, {fixed})
      // console.log(response);
    } catch (error) {
      console.log('Axios Error : ', error);
    }
  }

  const checkHandler = (e,name) => {
    setIsChecked(!isChecked);
    handleFixedExtension(name, e.target.checked);
  }

  return (
    <div>
      <h2>1번 고정확장자</h2>
      <div>
        {fixedNameList.length === 0 ? (
          <p>Loading...</p>
        ) : (
          <div>
            {fixedNameList.map((name, idx) => (
              <div className='checkbox' key={idx} style={{ margin: '0 10px 0 0' }}>
                <input
                  type="checkbox"
                  id = {name}
                  checked={fixedList.includes(name)}
                  onChange={(e) => checkHandler(e,name)}
                />
                <label htmlFor={name}>{name}</label>
              </div>
            ))}
          </div>
        )}
      </div>

      <div>
        <input
          type="checkbox">
        </input>

        <input
          type="checkbox">
        </input>

        <input
          type="checkbox">
        </input>
      </div>

      {/* <div>
        <h2>2번 커스텀확장자</h2>
        <input
          type="text"
          value={newExtension}
          onChange={(e) => setNewExtension(e.target.value)}
          maxLength={20}
        />
        <button style={{ background: 'grey', color: 'white' }}>
          + 추가
        </button>
      </div>
      <div>
        <h2>3번</h2>
        {customExtensions.map((extension, index) => (
          <button key={index}>
            {extension} X
          </button>
        ))}
      </div> */}
    </div>
  );
}

export default App;
