import React, { useEffect, useState } from 'react';
import axios from 'axios'

const backendUri = 'http://localhost:5001'

function App() {
  const [customExtensions, setCustomExtensions] = useState([]);
  const [newExtension, setNewExtension] = useState('');
  const [fixedList, setFixedList] = useState([]);
  const [isChecked, setIsChecked] = useState(false);

  useEffect(() => {
    fetchFixedExtensions();
  }, []);

  const fetchFixedExtensions = async () => {
    try {
      const response = await axios.get('/fixed/list');
      setFixedList(response.data);
      console.log('했다!!!')
    } catch (error) {
      console.log('Axios Error : ', error);
    }
  }


  const handleFixedExtension = async (fixed, isChecked) => {
    try {
      if (isChecked) {
        setFixedList((prev) => [...prev, fixed])
      }

      if (!isChecked && fixedList.includes(fixed)) {
        setFixedList(fixedList.filter((item) => item !== fixed));
      }
      const response = await axios.post(`${backendUri}/update/fixed`, {fixed})
      console.log(response);
    } catch (error) {
      console.log('Axios Error : ', error);
    }
  }

  const checkHandler = (e,fixed) => {
    setIsChecked(!isChecked);
    handleFixedExtension(fixed, e.target.checked);
  }

  return (
    <div>
      <h2>1번 고정확장자</h2>
      <div>
        {fixedList.length === 0 ? (
          <p>Loading...</p>
        ) : (
          <div>
            {fixedList.map((extension) => (
              <div className='checkbox' key={extension.id} style={{ margin: '0 10px 0 0' }}>
                <input
                  type="checkbox"
                  id = {extension.name}
                  checked={fixedList.includes(extension)}
                  onChange={(e) => checkHandler(e,extension)}
                />
                <label htmlFor={extension.name}>{extension.name}</label>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* <div>
        <h2>2번 커스텀확장자</h2>
        <input
          type="text"
          value={newExtension}
          onChange={(e) => setNewExtension(e.target.value)}
          maxLength={20}
        />
        <button style={{ background: 'grey', color: 'white' }}>
          + 추가
        </button>
      </div>
      <div>
        <h2>3번</h2>
        {customExtensions.map((extension, index) => (
          <button key={index}>
            {extension} X
          </button>
        ))}
      </div> */}
    </div>
  );
}

export default App;
